const hamb=document.querySelector("#hamb"),popup=document.querySelector("#popup"),body=document.body,menu=document.querySelector("#menu").cloneNode(1);function hambHandler(e){e.preventDefault(),popup.classList.toggle("open"),hamb.classList.toggle("active"),body.classList.toggle("noscroll"),renderPopup()}function renderPopup(){popup.appendChild(menu)}hamb.addEventListener("click",hambHandler);const links=Array.from(menu.children);function closeOnClick(){popup.classList.remove("open"),hamb.classList.remove("active"),body.classList.remove("noscroll")}links.forEach((e=>{e.addEventListener("click",closeOnClick)}));var swiper=new Swiper(".mySwiper",{cssMode:!0,navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},pagination:{el:".swiper-pagination"},mousewheel:!0,keyboard:!0,slidesPerView:3,spaceBetween:20,breakpoints:{1100:{slidesPerView:3},768:{slidesPerView:2},568:{slidesPerView:1},320:{slidesPerView:1}}});